--
-- Tabellenstruktur für Tabelle `pages`
--

CREATE TABLE `pages` (
  `id` int(10) UNSIGNED NOT NULL,
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `content` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `format` enum('2x2','3x3','4x4','5x5','6x6','7x7','8x8','9x9','10x10') COLLATE utf8mb4_unicode_ci NOT NULL,
  `format_desc` enum('Elimination','Double Elimination') COLLATE utf8mb4_unicode_ci NOT NULL,
  `prizes` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `game` enum('dota','csgo','fortnite','pubg') COLLATE utf8mb4_unicode_ci NOT NULL,
  `start_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `changed_by` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'Demo User',
  `status` enum('Предстоящий','Активный','Прошедший') COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

