-- Daten für Tabelle `pages`
--

INSERT INTO `pages` (`id`, `title`, `content`, `format`, `format_desc`, `prizes`, `game`, `start_at`, `changed_by`, `status`, `created_at`, `updated_at`) VALUES
(1, 'PUBG WTF CUP 1', '<p>\n                            Турнир будет включать групповой этап и плей-офф. На первой стадии соревнований будет использоваться система GSL, и преодолеют её 6 коллективов — по 3 из каждой группы. При этом победители своих групп сразу отправятся в полуфинал, а 2 и 3 места – в четвертьфинал. Все игры пройдут в формате best of 3.\n                        </p>\n                        <p>\n                            Что касается плей-офф, то здесь будет разыграна классическая сетка single elimination, best of 3. Уверены, что наши ребята приложат все усилия, чтобы порадовать болельщиков прекрасной игрой и одержать свою первую победу в новом составе\n                        </p>\n                        <h2>Подробнее о формате проведения</h2>\n                        <ul>\n                            <li>— 2 группы по 4 команды;</li>\n                            <li>— все матчи будут сыграны в формате double elimination, best of 3;</li>\n                            <li>— лучшие команды каждой группы переходят в полуфинал;</li>\n                            <li>— команды, занявшие 2 и 3 места, переходят в четвертьфинал;</li>\n                            <li>— худшие две команды каждой группы покидают соревнования;</li>\n                        </ul>', '2x2', 'Elimination', '1000p', 'fortnite', '2020-04-25 14:32:47', 'Yar', 'Активный', '2020-04-21 02:03:08', '2020-04-24 11:50:23'),
(2, 'DynoCup', 'asdddddddddddddddddddddddddddddddddddddddddddddddddddddd<br>', '6x6', 'Elimination', 'Сертификат на покупки', 'fortnite', '2020-04-21 14:32:47', 'Demo User', 'Предстоящий', '2020-01-29 05:03:08', '2020-04-21 18:41:38'),
